package pro.tompark.leetcode.days30.may;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

/**
 * Find All Anagrams in a String
 *
 *
 * Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.
 *
 * Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.
 *
 * The order of output does not matter.
 *
 * Example 1:
 * Input:
 * s: "cbaebabacd" p: "abc"
 * Output:
 * [0, 6]
 *
 * Explanation:
 * The substring with start index = 0 is "cba", which is an anagram of "abc".
 * The substring with start index = 6 is "bac", which is an anagram of "abc".
 *
 *
 * Example 2:
 * Input:
 * s: "abab" p: "ab"
 * Output:
 * [0, 1, 2]
 *
 * Explanation:
 * The substring with start index = 0 is "ab", which is an anagram of "ab".
 * The substring with start index = 1 is "ba", which is an anagram of "ab".
 * The substring with start index = 2 is "ab", which is an anagram of "ab".
 */
public class Day17FindAllAnagramsInAString {

    public static void main(String[] args) {
        String s = "cbaebabacd";
        String p = "abc";

        s = "";
        p = "yqrbgjdwtcaxzsnifvhmou";

        List<Integer> anas = findAnagrams(s, p);
        for (Integer ana : anas) {
            System.out.print(ana + " ");
        }
    }


    static List<Integer> findAnagrams(String s, String p) {
        List<Integer> anagrams = new ArrayList<>();
        if (s == null || p == null || s.length() < p.length()) return anagrams;
        int[] sCount = new int[26];
        int[] pCount = new int[26];
        for (char c : p.toCharArray()) {
            pCount[c - 'a']++;
        }

        int plen =  p.length();

        for (int i = 0; i < s.length(); i++) {
            sCount[s.charAt(i) - 'a']++;

            if (i >= plen) {
                sCount[s.charAt(i - plen) - 'a']--;
            }

            if (Arrays.equals(sCount, pCount)) {
                anagrams.add(i - plen + 1);
            }
        }

        return anagrams;
    }

    static List<Integer> findAnagrams1(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (s == null || p == null || s.length() < p.length()) return result;

        int[] charCount = new int[26];
        for (char c : p.toCharArray()) {
            charCount[c - 'a']++;
        }

        int left =  0;
        int right = 0;
        int count = p.length();

        while (right < s.length()) {
            if (charCount[s.charAt(right) - 'a']-- >=  1) count--;

            if (count == 0) result.add(left);

            if (right - left == p.length() && charCount[s.charAt(left++) - 'a']++ >= 0) count++;
        }

        return result;
    }

    static List<Integer> findAnagrams2(String s, String p) {
        List<Integer> anagrams = new ArrayList<>();
        if (s == null || p == null || s.length() < p.length()) return anagrams;
        char[] pp = p.toCharArray();
        Arrays.sort(pp);
        int len =  p.length();

        for (int i = 0; i < s.length(); i++) {
            int end = i + len;
            if (end <= s.length()) {
                String ss = s.substring(i, end);
                if (isAnagram(ss, pp)) {
                    anagrams.add(i);
                }
            }
        }

        return anagrams;
    }

    static boolean isAnagram(String s, char[] pp) {
        if (s == null || s.length() != pp.length) return false;

        boolean isAnagram = true;
        char[] ss = s.toCharArray();
        Arrays.sort(ss);

        for (int i = 0; i < ss.length; i++) {
            if (ss[i] != pp[i]) {
                isAnagram = false;
                break;
            }
        }

        return isAnagram;
    }
}